import React from 'react'
import { useEffect } from 'react';
import {ZoomMtg} from '@zoomus/websdk';
import { useState } from 'react'

const Zoom = () => {

    //loading zoom libraries before 
    const [joinMeeting, setJoinMeeting] = useState(false);


    useEffect(()=>{
        ZoomMtg.setZoomJSLib('node_module/@zoomus/websdk/dist/lib', '/av')
    },[])
  return (
    <div>
   
 
      <button style={{
            border: '2px solid blue',
            padding: '10px 20px',
            borderRadius: '15px',
            backgroundColor: 'blue',
            color: 'green',
            fontSize: '20px',
            fontWeight: 'bold',
          }} onClick={()=>{joinMeeting()}}>
        Zoom
      </button>
    </div>
  )
}

export default Zoom
