import React from 'react'
import { useEffect } from 'react';
import { ZoomMtg } from '@zoomus/websdk';
import { useState } from 'react'




const signatureEndpoint = 'https://localhost:4000'
const apiKey = 'JWT_API_KEY'
const meetingNumber = '123456789'
const role = 0
const leaveUrl = 'http://localhost:3000'
const userName = 'React'
const userEmail = ''
const passWord = ""
const signature = ''

const Zoom = () => {

    //loading zoom libraries before joining
    const [joinMeeting, setJoinMeeting] = useState(false);


    useEffect(() => {
        showZoomDIv();
        ZoomMtg.setZoomJSLib('node_module/@zoomus/websdk/dist/lib', '/av')
        ZoomMtg.preLoadWasm();
        ZoomMtg.prepareJssdk();
        initiateMeeting();

    }, [])

    const showZoomDIv = () => {
        document.getElementById('zmmtg-root').style.display = 'block';
    }

    const initiateMeeting = () => {



        ZoomMtg.init({
            leaveUrl: leaveUrl,
            success: (success) => {
                console.log(success)

                ZoomMtg.join({
                    signature: signature,
                    meetingNumber: meetingNumber,
                    userName: userName,
                    sdkKey: sdkKey,
                    userEmail: userEmail,
                    passWord: passWord,
                    success: (success) => {
                        console.log(success)
                    },
                    error: (error) => {
                        console.log(error)
                    }
                })

            },
            error: (error) => {
                console.log(error)
            }
        })
    }


    return (
        <div>


            <button style={{
                border: '2px solid blue',
                padding: '10px 20px',
                borderRadius: '15px',
                backgroundColor: 'blue',
                color: 'green',
                fontSize: '20px',
                fontWeight: 'bold',
            }} onClick={() => { joinMeeting() }}>
                Zoom
            </button>
        </div>
    )
}

export default Zoom
