import { useState } from "react";

function App() {
  const [open, setOpen] = useState(false);
  const Menus = [
    { title: 'Dashboard', src: 'logo192' },
    { title: 'inbox ', src: 'logo192' },
    { title: 'ACCOUNTS', src: 'logo192' },
    { title: 'SCHEDULE', src: 'logo192' },
    { title: 'SEARCH', src: 'logo192' },
    { title: 'ANALYTICS', src: 'logo192' },
    { title: 'ADS', src: 'logo192' },
    { title: 'FILES', src: 'logo192' },
    { title: 'BACKUP', src: 'logo192' },
    { title: 'SETTINGS  ', src: 'logo192' },
  ]

  const handleClick = () => {
    setOpen(!open)
  }
  return (

    <div className="flex">
      <div className={`${open ? 'w-72' : 'w-20'}      bg-dark-purple h-screen w-72 relative text-white`}>
        <img src="./logo192.png" onClick={handleClick} alt="dravyafolio" className={`right-0 absolute cursor-pointer h-5 w-5 top-9 rounded-full border-2 border-dark-purple ${!open && 'rotate-180'}`} />
        <div className=" flex gap-4 items-center">
          <img src="./logo512.png" alt="dravyafolio" className={`cursor-pointer duration-500 ${open && " rotate-[180deg]"}`} />
        <h1 className={`text-[#e92c2c] origin-left font-medium text-xl duration-100 ${!open && "scale-0"} `}>Designer</h1>
        </div>
        {/* <div className="p-7 text-2xl flex-1  h-screen font-semibold">
          <h1>Home Page</h1>
        </div> */}
        <ul className="pt-6">

          {Menus.map((Menu, index) => (
            <li className=" " key={index}>

              <img src="logo192.png" alt="dravyafolio" className="" />
              <span className={`${!open && "hidden"} origin-left duration-200`}>
                {Menu.title}
              </span>
            </li>
          ))}
        </ul>
      </div>

      <div className="h-screen flex-1 p-7"> 
            <h1 className="text-2xl font-sb">Home Page</h1>
      </div>
    </div>


  );
}

export default App;
